INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000367','수타사 산소길','수타사 산소길','수타사주차장~계곡길~용담~鈒?鈒?출렁다리~목교~계곡길~수타사생태숲~수타사~수타사 주차장','강원 홍천군','쉬움','5~10Km미만','6','수타사 인근 수타계곡이 흐르면서 바위아래로 떨어지는 계곡물로 인하여 생긴 용담은 물속아래 바위굴이 있어 그 굴이 수타사 아래까지 연결되어 있고 그 굴은 용이 살고 있는 거처로 용의 꼬리가 수타사 대웅전 아래까지 미쳐있다고 전해지고 있으며 이무기가 용이 되어 승천했다는 전설이 전해져 내려오고 있다. 신라의 원효대사가 창건한 것으로 전해지는 천년고찰인 수타사는 영서지방의 사찰 중 가장 오래된 사찰이며 보물 745-5호로 지정된 월인석보가 소장되어 있는 곳이다. 수타사 주변으로는 공작산을 배경으로 한 공작산 생태숲이 조성되어 있어 자연생태, 문화, 역사를 체험할수 있는 곳이다. 2012년에 놓은 굉소출렁다리가 새로운 명물로 각광받고 있다.','1시간 30분','수타사 생태숲에만 있으니 미리 구입하거나 사전 준비를 권장함','수타사 주차장, 수타사 생태숲','수타사 주차장 상가내에 매점이 있음','강원 홍천군 동면 덕치리 산 4',37.6976733,127.9624179);
