INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000374','인천둘레길','05코스(만월산 ~ 인천대공원)','신명요양원(횡단보도)→약사사 입구→인천사회복지회관 뒷길→향촌 마을 뒷길→주안산길(연결통로)→만월산 터널 입구→도룡뇽 마을→불로약수터→만수산 정상→송수천약수터→미추홀 학교(성현로)→수현부락길→인천수목원→장미원→호수광장','인천 부평구','보통','5~10Km미만','9.8','인천에서 도롱뇽이 가장 많이 서식하는 만삼이네 마을을 지난다. 제철에는 도롱뇽의 모습을 관찰할 수 있다. 별고개와 무너미고개의 전설로 과거 이 지역이 서울에서 인천 앞바다를 연결하여 주요 교통로였음을 알 수 있다. 5코스는 인천둘레길 5코스는 간석3동에 위치한 약사사에서 시작해 향촌마을 뒷길, 만월산 터널입구, 만수산 도룡뇽마을을 지나 불로약수터, 수현부락길, 인천수목원을 거쳐 인천대공원 정문 앞에서 끝나는 여정이다.길이 험하지 않고 평탄해서 누구나 가벼운 마음으로 걸을 수 있다.','3시간10분','불로약수터, 송수천 약수터','약사사입구, 인천대공원','','인천 계양구 임학동',37.5486073,126.7299616);
