INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000391','인천둘레길','01코스 계양산','연무정→무당골고개→청수수목원→고랑재고개→목상동(이주단지)→솔밭쉼터(반딧불 서식지)→피고개→중심성지(귀룽나무 쉼터)→징맹이고개→계양산 삼림욕장(지선사)→계양문화회관→연무정','인천 계양구','보통','5~10Km미만','6.05','부평의 진산인 계양산을 도는 코스로, 인천지역에서는 가장 생태적 환경이 우수하다. 계양산성, 징맹이고개, 이규보시비 등이 있어 삼국시대부터 근대에 이르기까지 부평의 역사를 담고 있는 곳이다. 계양산은 해발 395m인 산으로써 인천시내에서 가장 높은 산으로 인천을 대표하는 산이다. 진달래가 계양구를 상징하는 꽃이듯이 이 산에는 유난히 진달래가 많고, 특히 팔각정부근에는 무리를 지어 아름다움을 뽐낸다. 정상에 오르면 사방이 막힘없고 서쪽으로 영종도, 강화도등 주변 섬들이 한눈에 들어오며, 동쪽으로는 김포공항을 비롯한 서울시내전경이 자리를 잡고, 북쪽으로는 고양시가지가, 남쪽으로는 인천시내가 펼쳐진다. 주능선 등산로는 나무그늘이 없어 여름에는 산행이 적합치 않다.','2시간','','계양공원 연무정, 산림욕장','','인천 계양구 임학동',37.5486073,126.7299616);
