INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000406','부락산 둘레길','01코스 시내길','초록도서관~(1.1km)평택도립도서관~(0.5km)송탄중학교~(0.9km)초록도서관','경기 평택시','쉬움','1~5Km미만','2.5','낮은 등산로 코스와 다양한 운동 시설이 설치되어있어 가볍게 걷기에 좋은 길이다.','40분','없음','초록도서관 둘레길 시작지 1곳, 지산초등학교 지난후 1곳','평택도립도서관에서 1번국도 나가는 도보에 1곳, 1번국도변에 2곳있음','경기도 평택시 지산동 지산로 48',37.0807152,127.0600959);
