INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000407','부락산 둘레길','02코스 숲속길','초록도서관~(1.1km)평택도립도서관~(1.3km)이충분수공원~(2.0km)송탄중학교~(0.9km)초록도서관','경기 평택시','보통','5~10Km미만','5.3','도심과 숲속 매력 모두 느낄 수 있는 코스이다. 부락산을 가르는 코스로 등산을 좋아하는 사람들의 맞춤형 코스이다. 도보길을 걸은 후 이충분수공원의 아름다움을 만끽하고 부락산의 싱그러움을 느낄 수 있는 길이다.','1시간 30분','없음','초록도서관 입구1곳, 이충분수공원 1곳','이충분수공원 근처 내 편의점 다수, 부락산 내려오는 길에 음식점 있음','경기도 평택시 지산동 지산로 48',37.0807152,127.0600959);
