INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000411','강화나들길','03코스 능묘 가는 길','온수공영주차장~(0.2km)~성공회온수성당~(2.4km)길정저수지~(2.9km)이규보묘~ (3.8km)곤릉~(1.9km)석릉~(5km)가릉','인천 강화군','보통','15~20Km미만','16.2','강화 나들길 03코스 능묘 가는길. 길상면 온수리 공영주차장에서 전등사를 도는 코스(1시간소요)와 온수리 성공회 성당으로 바로 걷는 코스가있다. 길정저수지의 잔 물결을 따라 아름드리 당산 나무들이 우거진 마을을 지나게 된다. 하늘을 찌르듯 높다랗게 핀 등꽃을 볼수 있으며, 아름다운 숲길을 걷다 보면 개경으로 돌아가지 못한 슬품을 간직한 고려 왕릉들이 진강산 자락을 베게 삼아 누워서 나들길을 걷는 나들이꾼의 발소리를 말없이 반긴다..','5시간30분','식수보급처가 없으니 매점에서 구입하거나 사전준비','온수공영주차장,전등사,온수성공회성당,이규보묘, 가릉','온수공영주차장,전등사','인천 강화군 강화읍 신문리 253-5',37.7458046,126.4823094);
