INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000415','평택호관광지 수변테크','사색의 길','평택호관광안내소~(0.7km)전망대~(0.2km)요트선착장~(0.2km)한국소리터~ (0.1km)모래톱공원','경기 평택시','매우쉬움','1~5Km미만','1.5','평택호는 1973년 경기도 평택시 현덕면 권관리와 아산시 인주면 공세리 연결하는 방조제를 건설하면서 생긴 호수로 용인, 오산, 안성에서 발원한 지천이 평택호에 유입 농·공업용수를 저장할 수 있는 인공호수이다. 목조 수변테크를 따라 뱃머리 모양 전망대에서 넓게 펼쳐진 호를 보며 고사분수의 시원한 물줄기를 느끼며 걷다보면 한국소리터에 체험장과 공연장이 있으며, 평택호 예술관의 전시작품을 감상할수 있다.','40분','식수보급처가 없으니 매점에서 구입하거나 사전준비','한국소리터,가족공원, 요트선착장,예술관주차장','전망대부근, 자동차극장 주차장','경기 평택시 현덕면 권관리 321',36.921419,126.9222362);
