INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000416','대덕산 금대봉 생태탐방로','분주령 코스','두문동재~(1.2 ㎞)금대봉~(0.9㎞)고목나무샘~(2.6㎞)분주령~(1.4㎞)대덕산~(1.4㎞)분주령삼거리~(1.1㎞)세심교~(0.6㎞)검룡소~(1.4㎞)검룡소주차장','강원 태백시','보통','10~15Km미만','10.6','금대봉 (1,418.1m)과 대덕산(1,307m) 일대 이곳의 봄은 야생화들이 군락을 이루어 그야말로 천상의 화원을 이룬다. 또한 한강의 발원지인 검룡소가 위치해 있으며 하루 2천여톤의 물을 꾸준히 내뿜고 있어 자연의 신비로움을 더해주고 있어 해마다 탐방객들이 줄지어 방문하고 있다. 이 지역은 자연경관 및 식생이 우수하고 희귀동식물이 서식하고 있어 1993년 환경부가 강원도 생태경관 보전지역으로 지정하여 관리하여 오고있다. 강원 태백시에서는 이지역의 자연생태보호를 위하여 사전예약제로 운영하고 있으며 1일 탐방인원도 300명이하로 제한하고 있어, 탐방을 하고자 할 경우에는 인터넷으로 사전에 예약을 하여야 한다.','5시간','산행중 식수보급처가 없으니 미리 구입하거나 준비요망','검룡소 주차장','없음','강원 태백시 창죽동',37.238329,128.9181639);
--행 417
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000417','강화나들길','06코스 화남생가 가는 길','강화버스터미널~약수터~선원사지~삼동암천~환경농업교육관~화남생가~능내촌입구~광성보','인천 강화군','보통','15~20Km미만','15.8','강화 나들길 06코스 화남생가 가는길. 화남고재형 선생은 1906년 강화도의 유구한 역사와 수려한 자연을 노래하며 강화의 논.밭.산길.바닷길을 둘러보고 칠언절구의 한시 256수를 "심도기행"이라는 기행시집으로 남겼다. 강화 나들길은 바로 화남 선생이 걸었던 길을 잇고 잊혀진 길을 연결한 길이다. 선생의 생가 두두미 마을에는 선생이 남긴 "봄바람 맞으며 두두미를 걷노라니 온 마을의 산과 내가 한눈에 들어오네. 밝은달 푸른버들 여러(구)씨 탁상에서 잔 가득한 술맛이 힘을 내게 하는구나"라는 "두두미동" 시 한수가 있다','5시간30분','식수보급처가 없으니 강화터미널 매점에서 구입하거나 사전준비','강화버스터미널,선원사,두운리마을회관,두두미마을,광성보','강화터미널,광성보','인천 강화군 강화읍 신문리 253-5',37.7458046,126.4823094);
