INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000422','쇠둘레 평화누리길','02코스 금강산 가는 길','오덕리(강회동)~(3km)학저수지(도피안사) ~ (2.5km)노동당사 ~ (5.5km)용담','강원 철원군','보통','10~15Km미만','11','평화 누리길 02코스 금강산 가는길. 한탄강의 주상절리 등 뛰어난 자연경관이 함께하는 농촌마을(강회동)부터 탐방로가 시작되어 철원평야의 들녘을 감상하는 농로길을 체험합니다. 1921년 일제가 설치한 인공저수지로 철새와 희귀 야생동식물이 잘 보존된 학저수지를 지나 1000년 고찰 도피안사(국보 및 보물소재)를 경유합니다. 또한, 서태지와 아이들의 뮤직비디오와 열린음악회 개최지로 유명한 근대문화유적지의 대표 철원노동당사를 지나 일제시대 상수도 공급시설인 수도국지, 주민의 생활로인 새우젓고개를 지나며 옛 철원의 의미와 가치를 느낄 수 있습니다. 최종 목적지인 용담은 과거 대 문학가인 상허 이태준 선생의 생가터가 있는 곳입니다. 금강산 가는길 체험을 통해 철원의 역사와 문화, 자연생태를 잘 체험할 수 있는 곳입니다.','4시간','2개소(도피안사 주차장, 노동당사)','2개소(도피안사 주차장, 노동당사)','1개소(노동당사)','강원 철원군 동송읍 장흥리 2450',38.2009105,127.2731334);
