INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000429','강화나들길','10코스 교동도 머르메 가는 길','대룡리~(4,3km)난정저수지~(2.1km)수정산~(2.5km)금정굴~(1.8km)애기봉~(1km)죽산포~(1km)머르메~(1.3km)양갑리마을회관~(1.7km)미곡종합처리장~(1.5km)대룡리','인천 강화군','보통','15~20Km미만','18.96','강화 나들길 10코스 교동도머르메 가는길. 교동도는 잃어버린 과거를 찾을수 있는 곳이라고 흔히 말한다. 6.25때 활주로로 사용하였던 곧게 뻗은 11번 도로를 따라 드넓게 펼쳐진 교동 평야를 가로 지르면 거대한 호수를 연상케하는 난정저수지를 만난다. 부지런하고 온순했던 선조의 숨결이 유적으로 남아있고 산과 들과 바다가 어우러진 머르메 가는길은 옛 사람들의 일상이 그대로 풍광이 되어 나들이꾼을 반긴다. 교동도 최대의 번화가는 대룡시장 골목이다. 이곳은 마치 1960-70년대 풍의 낡은 건물들과 정감어린 풍경들이 고스란히 남아있어 이곳에 있으면 마치 과거의 시간 속을 거니는 것만 같다. 이곳의 미장원, 이발소, 분식집, 잡화점이나 구멍가게 등은 ‘빈티지여행’을 좋아하는 사람들의 훌룡한 촬영 소재이기도 하다.','5시간','출발 지인 대룡시장 내 농협 하나로마트에서 구입하거나 사전 준비','대룡시장','대룡시장','인천 강화군 강화읍 신문리 253-5',37.7458046,126.4823094);
