INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000445','다산길','13코스 사릉길','사능역-된봉-관음봉-천마산정상(임도)-마치고개','경기 남양주시','매우어려움','15~20Km미만','15.2','다산길의 마지막 코스는 사릉길이다. 사릉길은 가파르기 때문에 장비를 갖춰 걷는 것이 안전하다. 경춘선 사릉역에서 46번국도 마치고개까지 총 거리가 15.2㎞로 7시간이 걸린다. 코스 대부분이 천마산에서 서쪽으로 흐르는 산줄기를 따라 이어지는 산길이다. 제법 가파르고 오르막길이 많으나 가는 곳 중간중간에서 진건읍, 호평, 평내의 도시지역을 조망할 수 있다.','약 7시간 35분','사전에 준비 필요','시능역','시능역(중간에 매점이 없으므로 간식은 사전에 준비 필요)','전남 화순군 화순읍 다지리 239-1',35.0497987,126.998084);
