INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000446','다산길','01코스 한강나루길','한강삼패지구-팔당역-역사박물관-능내역-운길산역','경기 남양주시','쉬움','15~20Km미만','16.7','한강삼패지구에서 팔당역, 팔당댐, 능내리를 지나 운길산역까지 한강과 북한강을 가장 가까이 보면서 이어진 길이다. 상팔당부터 13Km나 이어지는 중앙선 옛 철로가 있는 봉안터널은 영화속 한 장면을 연상케 하며 폐철로를 자전거도로로 조성하여 한강과 북한강의 정취를 즐기기에 좋으며 한강나루길코스는 한강과 팔당호의 풍광과 평탄한 코스여서 부담없이 걸을 수 있다.','5시간 30분','','','','전남 화순군 화순읍 다지리 239-1',35.0497987,126.998084);
