INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000449','다산길','03코스 새소리 명당길','능내리-율리고개 갈림길-조안초교 입구','경기 남양주시','보통','1~5Km미만','4.8','새소리명당길은 이름 그대로 새소리를 들을 수 있는 편안한 길이다. 예부터 산수 좋고 조용한 지역이라 새들이 쉬어가는 곳으로 유명해 붙여진 이름이다. 옛날 한양으로 가던 한 선비가 해가 저물어 이 마을로 들어온 후 새소리가 듣기 좋고 물이 좋아 가려던 길을 멈추고 눌러앉은 데서 비롯된 지명이다.','2시간','없음','늠내2리마을회관','없음','전남 화순군 화순읍 다지리 239-1',35.0497987,126.998084);
