INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000463','지리산둘레길','18코스(오미 ∼ 방광)','오미마을(운조루) ? 용두갈림길(1.1km) ? 상사마을(1.6km) ? 지리산탐방안내소(5km) ? 수한마을(3.2km) ? 방광마을(1.4km)','전남 구례군','보통','10~15Km미만','12.3','지리산 둘레길 18코스(오미 ∼ 방광). 섬지뜰 품고 가는 동네 마실길 전라남도 구례군 토지면 오미리 오미마을과 광의면 방광리 방광마을을 잇는 12km의 지리산둘레길. 이 구간은 전통마을의 흔적이 가장 많이 남아있는 구간 중 하나이다. 구례분지를 조망하며 농로와 숲길을 주로 걷는 아기자기함이 재미있다. 화엄사 아래 지리산탐방안내소에서는 지리산의 자원과 역사를 살피고, 종 복원센터에 들러 반달곰도 만날 수 있다. 상사, 하사, 오미마을에서는 운조루, 쌍산재, 곡전재 등 고택의 정취와 효와 장수의 고장다운 인정을 느낄 수 있다.','5시간','오미마을, 황전마을(지리산 탐방안내소)','오미마을, 황전마을(지리산 탐방안내소)','오미마을, 황전마을(지리산 탐방안내소)','전북 남원시 인월면 인월리',35.4592144,127.6018698);
