INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000464','살래길','살래길','살래1구간 2.6km(통일동산 중앙공원~고려역사박물관~파주NFC~검단사입구) ~ 살래2구간 1.6km(검단사입구~유승아파트입구) *순환형 코스','경기 파주시','쉬움','1~5Km미만','4.2','축구 국가대표 트레이닝 센터인 파주NFC 인근에 위치한 살래길은 어르신도 부담없이 즐길 수 있는 산책로입니다. 살래길이라는 이름은 몸의 한부분을 가볍게 잇따라 좌우로 흔드는 모양을 나타내는 의태어 ''살래살래''에서 따왔으며 구불구불 살래살래 함께 걷는 길, ''나''가 아닌 ''우리함께'', ''물음표 같은 길 되돌아 올때 느낌표 손에 쥐고 돌아오는 길''을 의미합니다. 살래길의 구불구불한 산책로는 두코스(순환형 코스) 연장4.2km로 약 1시간 30분 동안 살래살래 걸으며 즐길 수 있다는 것이 매력입니다.','1시간 30분','없음','검단사 부근 화장실 이용','유승앙브와즈 1단지 부근 마트','경기도 파주시 탄현면 법흥리',37.7731669,126.6896523);
