INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000465','개화산자락길','개화산무장애숲길','약사사 인근 무장애숲길 출발지 ~ 북카페 1 ~ 북카폐 2 ~ 하늘전망대','서울 강서구','매우쉬움','1Km미만','0.7','개화산은 강서구 방화동에 위치한 128m 높이의 나지막한 산이다. 개화산 자락길은 방원중학교 주변 개화산 입구부터 하늘길 전망대까지 총 2.1㎞이며, 이중 0.7㎞ 구간은 1.8m 폭으로 목재데크를 깔고 손잡이도 설치해 장애인도 쉽게 오를 수 있도록 했다. 또한 장애인 이용자의 편의를 돕기 위해 7곳의 휠체어 회 차 구간과 차량 3대를 주차할 수 있는 전용 주차장도 마련됐다. 사계절 아름다운 자연을 만끽할 수 있도록 자락길 주변에는 산벚나무, 산사나무, 맥문동 등 14종 3만 7천여 주의 관목·교목·초화류를 조성해 두었다. 또한, 숲속에서 독서를 즐기며 심신을 정화할 수 있도록 북카페도 두 곳에 설치했고, 이 곳에는 건강, 여가, 소설 등 책이 비치되어 있고, 테이블과 의자를 갖춰 누구에게나 편안한 힐링공간으로 꾸며져 있다.','30분','출발지 인근','출발지 인근 및 약사사','약사사','서울특별시 관악구 대학동 197',37.4564293,126.9457572);
--행 466
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000466','정선 산소길','01코스 동강길','정선5일장-병방치스카이워크(3.5)-동강할미꽃마을(5.2)-귤암캠핑장(3km)-가수느티나무(4.7km)-점재마을(9.6km)-나리소(2.8km)-제장마을(4.2km)','강원 정선군','쉬움','20~100Km미만','33','동강은 생태보전지구로 지정될 만큼 다양한 생물과 식물이 존재하고 수직절벽과 아름다운 산세와 맑은 강을 조망하면서 탐방할 수 있어 많은 사람들이 찾고 있다. 주변관광자원도 풍부하여 전국 최고의 재래시장인 정선5일장, 병방치 스카이워크, 동강전망대, 귤암캠핑장, 가수8경, 나리소 등의 볼거리와 귤암리 옛길, 수리봉, 백운산 등산로 등이 위치하여 다양한 코스를 즐길 수 있다. 코스가 길어 정선5일장 ~ 가수느티나무, 가수느티나무 ~ 제장마을 등의 코스로 나누어 트래킹하는 것이 좋음','8시간 30분','매점이 없어 식수와 도시락 개별 준비 (귤암쉼터, 마을 주변에 간이상수도 등 식수 이용은 가능)','5개소(스카이워크, 귤암쉼터, 캠핑장, 숲속동물쉼터, 가수분교)','스카이워크, 가수분교 옆 동강매점','강원 정선군 정선읍 귤암리 산 196-1',37.3381421,128.6140677);
