INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000479','군포수릿길','수리산둘레길','태을초~노랑바위~명상의 숲~상연사~용진사~임도오거리~감투봉 방향~밤바위산~시민체육광장','경기 군포시','보통','15~20Km미만','16','서울외곽순환고속도로 산본IC를 통해 군포로 들어오다 보면 제일 먼저 수리산과 만난다. 쭉 곧게 뻗어 내린 산등성이를 뒤덮은 짙은 녹음이 보기만 해도 시원하다. 군포에서는 어디를 가도 수리산 자락과 맞닿아 있다. 수리산둘레길은 대문 밖으로 나서면 가까이 만날 수 있는 우거진 숲과 수리산의 굽이굽이 오르막과 내리막이 반복되어 산속의 운치를 한껏 느낄 수 있다.','약 5시간 30분','수리약수터 2곳','태을초 뒤 초소 옆','없음','경기 군포시 산본동',37.3491521,126.9096961);
