INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000481','군포수릿길','임도길(구름산책길)','중앙도서관~임도5거리~덕고개~제4임도~행복쉼터~튤립나무숲~속달동마을길','경기 군포시','보통','1~5Km미만','4.8','수리산 임도는 산림관리를 위해 만들어진 길로 도로처럼 잘 다듬어진 평평한 길을 산책하듯 걷는 독특한 맛이 있다. 다리로는 평지를 걸으면서도 눈앞에 펼쳐진 풍경들은 산속을 느끼게 해주는 길이다. 사람왕래가 가장 많은 이길은 코스가 완만하고 8단지 아파트에서 바로 이어져 접근성이 좋다.','약 1시간 40분','8단지약수터','없음','없음','경기 군포시 산본동',37.3491521,126.9096961);
