INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000482','관동팔경 녹색경관길','동해 01코스(추암해변 ~ 묵호역)','추암해변-(6.9km)동해역-(2.9km)한섬해변-(3.2km)묵호역','강원 동해시','보통','10~15Km미만','13','관동팔경 녹색경관길 동해1코스와 해파랑길 33코스, 산소(O2)길 1코스와 동일한 구간입니다. 추암해변부터 묵호역까지는 전천변을 따라 걷다 동해시내에 잘 조성된 산책로를 따른다 걷는도중 만나는 동해역은 리모델링을 했음에도 정감넘치는 옛 역사의 모습을 간직하고 있다.','4시간 20분','식수 보급처가 업으니 추암해변, 동해역 매점등에서 구입하거나 사전준비','추암해변, 동해역, 한섬해변, 묵호역등','추암해변, 동해역, 묵호역등','강원 강릉시 남항진동',37.7647867,128.9538211);
