INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000485','군포수릿길','수리산 임도길(바람고개길)','납덕골주차장~바람고개길 입구~에덴기도원~납덕골주차장','경기 군포시','보통','5~10Km미만','5.6','수리산 임도길 중 가장 깊은 숲의 정취를 갖고 있는 이길은 납덕골에서 수리사 방향으로 5분쯤 올라간 곳에서 시작된다. 오래된 마을집의 낡은 담장에 알록달록한 그림들로 가득하다. 납덕골 벽화를 지나 수리사 방향으로 가면 숲의 기운을 잔뜩 머금은 촉촉한 습기와 울창한 수림, 그러나 험난한 등산로가 아닌 잘 다듬어진 완만한 산책로 이다. 인위적인 손길이 더해지지 않은 자연그대로의 숲길이라는 점과 인적이 드문 고즈넉한 분위기가 이 길의 매력이다.','2시간','바람고개약수터 1곳','납덕골주차장','없음','경기 군포시 산본동',37.3491521,126.9096961);
