INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000500','울주옛길','범서옛길 1코스','갓골 ~ 강당대밭 ~ 선바위 ~ 종디미골 ~ 종디미만디 ~ 도는고개 ~ 긴밭골 ~ 구태골 ~ 안골 ~ 솔밭골 ~ 오심동만디 ~ 재영골 ~ 말구부리 ~ 장구산 ~ 불성골만디 ~ 범서산성 ~ 배리골 ~ 다진차밭 ~ 다전마을','울산 울주군','보통','5~10Km미만','8','울주옛길은 단순히 걷기 위한 길도 아니고, 특정한 경관이나 장소를 홍보하기 위한 길이 아니며, 마을과 마을 이야기와 이야기를 이어주던 옛 길을 찾은 것으로, 풍경이나 역사, 문화자산만큼 가치 있는 지역의 생활문화가 녹아 있는 길이라고 할 수 있다. 이 길은 과거게 주민들이 걸었던 길을 현 시점에 우리가 의미를 가지고 다시 걸을 수 있는 이야기가 있는 길에 있어, 마을주민들과 함께 조사하고 선정한 길이라서 더욱더 특색이 있다구 할 수 있다. 범서는 태화강 중류지역에서 산사시대부터 마을을 이루며 성장하였으며, 삼한시대 진한의 땅으로 굴아화촌으로 불려졌다. 범서옛길은 2009년부터 마을주민이 발굴한 옛길로 더욱 가치가 있다.','3시간','','선바위도서관(인근) *코스 중간에는 없음','갓골공원 일대, 다전마을 일대 *코스 중간에는 없음','울산 울주군 범서읍 구영리 204-2',35.5827367,129.2540564);
