INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000501','나래길','01코스','명덕호수공원~(0.5km)휴게소 1~(1.5km) 휴게소 2~ (0.7km)큰마을저수지삼림공원','울산 동구','보통','1~5Km미만','2.7','‘나래’날개를 이르는 말, 명덕호수공원과 큰마을 저수지 산림공원이 날개의 형태를 이루며 연결되어 있다. 아름다운 나래길을 걸으면 스트레스로 움츠려진 어깨를 활짝 펴게 되고 삶의 활력을 찾게 된다는 의미이다. 동구를 대표하는 휴식처로서의 비상을 기원한다.','48분','없음','큰마을 저수지 삼림공원 입구','없음','울산 동구 서부동',35.528023,129.4152448);
