INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000510','영남알프스 하늘억새길','01코스 억새 바람길','간월재~신불산~신불재~영축산(4.5km)','울산 울주군','보통','1~5Km미만','4.5','간월재를 출발하여 신불산, 신불재를 거쳐 영축산 까지 가는 코스로 영남알프스 주 능선을 걷는 코스이다. 800m이상의 고산지대에 형성된 수 십만평의 억새와 단조 늪, 단조산성 등 역사, 문화 자원을 볼 수 있다. 간월재는 10만여평의 억새평원이 있어 억새꽃이 만발하는 가을이면 산상음악회,패러글라이딩 대회가 열려 많은 사람들이 찾고 있는 명소입니다. 그리고 신불공룡능선은 칼바위 능선이라고도 하는데 영남알프스에서 가장 험하고 멋있는 긴 능선으로 손꼽히고 있습니다. 또한 60여만평의 신불평원은 울산 12경의 하나에 들만큼 억새가 바람에 날리는 멋진 풍광을 자랑하는 곳입니다.','2시간','식수보급처가 없으니 간월재 휴게소에서 구입하거나 사전준비, 간월재, 신불재 약수터 각각 1개','간월재 대피소 겸 화장실 1동','출발점인 간월재휴게소 , 중간, 도착점에는 없음','울산 울주군 상북면 이천리',35.5342077,129.0099453);
--행 511
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000511','영남알프스 하늘억새길','02코스 단조성터길','영축산~단조성~청석골~파래소2교~죽전마을(6.6km)','울산 울주군','쉬움','5~10Km미만','6.6','영남알프스 하늘억새길 제2구간은 단조성터가 펼쳐집니다. 이 구간은 우리 역사를 되새기며 걷기에 좋은 길로 이름이 나 있습니다. 영축산과 단조성터, 휴양림, 죽전마을로 이어지는 6.6km구간의 탐방로입니다. 죽전마을과 영축산 중간지점의 ''단조성터''를 찾아가는 길로 역사적 의미를 되 새길수 있는 곳으로 이 구간의 단조산성은 신라시대 때부터 축조되었다고 추정되며, 신불평원에서 영축산 정상으로 금강골이 지리하고 있고 이곳에는 4개의 암봉이 있어 전국의 전문 산악 클라이머 들이 즐겨 찾는 곳이기도 합니다.','3시간','식수보급처가 없으니 간월재 휴게소에서 구입하거나 사전준비, 단조성터 약수터 1개','신불산폭포휴양림(하단)내 화장실 이용','출발점, 중간에는 매점이 없으며, 도착지점 죽전마을에는 주변 상점등이 다소 없음','울산 울주군 상북면 이천리',35.5342077,129.0099453);
--행 512
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000512','영남알프스 하늘억새길','03코스 사자평 억새길','죽전마을~향로봉삼거리~사자평~재약산~천황재~천황산','울산 울주군','보통','5~10Km미만','6.8','제3구간 ''사자평 억새길''은 전국 제일의 억새평원으로 하얀 억새꽃이 장관을 이루는 곳인데요 눈으로 즐기고 가슴으로 감동하는 사자평 억새길은 재약산, 천황재, 천황산 사이에 위치한 곳으로 억새평원이 장관을 이루는 6.8km 구간의 탐방로입니다. 재약산 정상인 수미봉에서 표충사 쪽은 수십길의 바위 절경이 펼쳐져 있고 북쪽으로는 천황산이 우뚝 솟아 있으며 중간에 천황재가 내려다 보이는 곳입니다. 산세가 수려하여 삼남금강이라 부르며, 1,000m이상의 준봉들로 이루어진 영남알프스 산군에 속하며, 재약산 하단에 위치한 평원은 사자평으로 불리고 그 억새평원에 자리잡은 재약산 사자평 고산습지는 환경부에서 지정한 보호습지로 보존가치가 아주 높다.','1시간','식수보급처가 없으니 사전준비, 주변 약수터 없음','3구간내 화장실 없음','출발점, 중간, 도착점에도 매점이 없음','울산 울주군 상북면 이천리',35.5342077,129.0099453);
--행 513
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000513','영남알프스 하늘억새길','04코스 단풍 사색길','천황산~샘물상회~밀양케이블카~능동산~배내고개(7km)','울산 울주군','쉬움','5~10Km미만','7','영남알프스 하늘억새길 제4구간은 특히 가을단풍이 아름다운 곳으로 등산객들의 사랑을 듬뿍 받고 있는 길입니다. ''단풍사색길''은 천황산과 샘물상회를 그쳐 능동산과 배내고개에 이르는 7km 구간으로 천황산에서 배내고개까지 길은 사시사철 아름답지만, 특히 가을 단풍이 절경을 이루는 곳입니다. 천황산 요지군은 조선시대 백자를 굽던 우리의 소중한 유산인 가마터입니다. 그리고 샘물상회는 목조건물로 지어진 작은 쉼터로 영남알프스를 찾는 산악인들에게 사랑받고 있는 공간입니다.','2시간30분','배내고개 휴게소에서 구입하거나, 샘물상회에서 구입 가능, 능동산 약수터 1개 있음','샘물상회 간이화장실 1동, 배내고개 수세식 화장실 1동','출발점에는 매점이 없으나 중간지점에 샘물산장이 있으며, 도착지점인 배내고개에 휴게소가 있어 먹거리가 있음','울산 울주군 상북면 이천리',35.5342077,129.0099453);
--행 514
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000514','영남알프스 하늘억새길','05코스 달오름길','배내고개~배내봉~간월안부~간월산~간월재(4.8km)','울산 울주군','보통','1~5Km미만','4.8','영남알프스 하늘억새길 제5구간 ''달오름길''은 배내봉을 지나 간월재에 이르는 4.8km 구간의 탐방로입니다. 배내고개는 하늘의 기운을 받는 곳이란 믿음이 서려 있는 곳으로 사시사철 전국에서 많은 관광객과 등산객들이 즐겨찾는 명소입니다. 간월산은 신불산과 더불어 신성한 산이라는 의미를 가지고 있는 산으로 동쪽은 깎아 지른듯한 바위 절벽이 장관을 이루고 서쪽은 경사가 완만한 공원지대를 이루고 있습니다. 달 오름길은 영남알프스 하늘억새길의 마지막 구간으로 4시간여가 소요되는 신비롭고 아름다운 길입니다.','4시간','배내고개 입구 매점, 간월재 휴게소에서 구입하거나 사전준비, 간월재 약수터 1개','배내고개 등산로 입구 화장실 1동, 간월재 휴게소 화장실(1동) 이용','출발점, 도착지점에는 휴게소가 있어 음식물 구입이 가능하나 중간지점에는 없음','울산 울주군 상북면 이천리',35.5342077,129.0099453);
