INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000516','유교문화길','02코스 하회마을길','안동한지-0.5-소산마을(삼구정) -5.6-병산서원-5-만송정-1.7-하회마을장터-0.8-현회 삼거리','경북 안동시','쉬움','5~10Km미만','13.7','유교 문화길 02코스 하회마을길. 안동 한지에서 현외에 이르는 하회마을길은 낙동강(화천)을 따라 하회마을을 굽어보는 산길과 들길이 잘 남아있는 구간이다. 신라 김은열을 시조로 하는 선안동김씨와 고려 개국때 공을 세운 태사 김선평을 시조로 하는 후안동 김씨들이 함께 살고 있는 소산마을과 풍산류씨들이 600여년 동안 살고 있는 하회마을의 문화유산을 볼 수 있다. 아름다운 효행의 이야기가 전하는 삼구정, 청음 김상현의 청원루, 비안공 김삼근이 지었다는 돈소당, 양소당 종택, 상락김씨 김용추가 지은 삼소재, 묵재고택, 동야고택 등 많은 문화재들이 남아 있다. 그리고 자연 환경과 잘 어우러진 병산서원과 물돌이 마을의 특이한 모습을 볼 수 있다.','4시간','식수보급처가 없으니 매점에서 구입하거나 사전준비','안동한지, 병산서원, 하회마을','출발 전 안동시내 시장 이용,하회장터 활용 가능','경북 안동시 풍천면 병산리 산 49',36.5380851,128.5434305);
