INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000594','도심건강길','03코스 푸른길','조대입구 ~ 농장다리 ~ 산수도서관 ~ 두산위브 ~ 금호아파트 ~ 푸른길공원 ~ 광주역','광주 동구','매우쉬움','1~5Km미만','2.8','푸른길은 도심을 통과하던 경전선을 폐선한 후 폐선 부지를 활용하여 조성된 도시공원인 광주푸른길공원의 일부이다. 느티나무, 때죽나무, 자귀나무, 단풍, 동백 등의 수목들과 맥문동, 벌개미취, 원추리 등의 야생화들로 가득 차있어 사람과 새, 벌, 나비들을 불러들이는 생명의 공간인 푸른 길에는 자연의 향기가 가득하다. 코스 말미에는 푸른길의 이름을 그대로 옮긴 공원 푸른길 공원이 있는데 주민의 힘으로 기찻길 부근 마을에 도시 숲길, 마을 텃밭, 쉼터, 카페 등을 만들어 휴식 공간은 구간마다 만들었다.','30분','조선대 부근 상점, 이마트 동광주점','계림 2동 주민센터, 이마트 동광주점','조선대 부근 상점, 이마트 동광주점','광주 동구 선교동',35.0834578,126.9533397);
