INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000596','서울도보관광코스','01코스 청계천','청계광장-광통교-삼일교-수표교-새벽다리-오간수교-동대문 DDP','서울 종로구','쉬움','1~5Km미만','2.7','분수와 폭포, 청계천 미니어처 등으로 꾸며져 환상적인 공간을 연출하는 청계광장을 비롯하여 95년 만에 복원된 조선시대 대표적인 석교인 광통교, 정조가 어머니 혜경궁 홍씨를 모시고 화성과 현륭원을 다녀온 모습을 그린 정조반차도, 빨래터, 소망의 벽, 하늘물터, 버들습지로 이루어진 ‘청계천 8경’을 감상할 수 있습니다.','2시간','없음','청계천에서 공공화장실 이정표로 안내','청계천 곳곳에 위치','서울 종로구 가회동',37.5825259,126.9850602);
