INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000601','도심건강길','07코스 읍성길','장동로터리 ~ 대한생명사거리 ~ 금남로공원 ~ 광주세무서 ~ 구시청사거리 ~ 문화전당 ~ 대성학원','광주 동구','매우쉬움','1~5Km미만','2.5','광주 옛 읍성터인 성곽길을 따라 조성된 길이다. 비롯 성곽의 흔적은 찾아볼 수 없으나 광주폴리 중 10개를 감상할 수 있는 코스이다.(‘폴리(Folly)’의 건축학적 의미는 본래의 기능을 잃고 장식적 역할을 하는 건축물을 뜻한다. 하지만 광주폴리는 공공 공간 속에서 장식적인 역할 뿐 아니라, 기능적인 역할까지 아우르며 도시재생에 기여할 수 있는 건축물을 의미한다.)','30분','인근 다수','금남로 지하상가 화장실','인근 다수','광주 동구 선교동',35.0834578,126.9533397);
