INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000602','도심건강길','08코스 전당 예술길','금남로공원 ~ 민주인권로 ~ 아시아문화의 전당 ~ 예술의거리 ~ 대인시장','광주 동구','매우쉬움','1~5Km미만','2','5·18민주화운동 기록물이 유네스코 세계기록유산에 등재됨을 기념해 지정한 유네스코 민주인권로(금남공원 ~ 구도청앞, 518m)와 예향의 전통과 멋이 살아 숨쉬는 예술의거리를 거쳐 대인시장 입구까지 이어진 길이다.','30분','인근 다수','금남지하상가 화장실','인근 다수','광주 동구 선교동',35.0834578,126.9533397);
