INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000620','덕음산 솔바람 건강길','덕음산 솔바람길','솔향기산림욕장 ~ 남원랜드 ~ 덕음산솔바람건강길 입구 ~ 숲길 ~ 남원우주항공천문대 주차장','전북 남원시','쉬움','1~5Km미만','1.2','솔바람 건강길은 덕음산 팔각정에 오르면 남원시내를 가로지르는 요천과 북으로 보절면 천황봉, 동으로는 고남산과 만복대, 남으로는 견두산과 천마산을 볼 수 있을 뿐 아니라 남원 시내를 한눈에 감상할 수 있고 각종 체육시설 및 등산로 등이 잘 조성되어있어 남원시민의 쉼터이자 산책로 역할을 하고 있다. 솔바람 건강길은 식물이 발산하는 호르몬 중에서 상쾌감을 느끼게하는 물질로써 소나무는 다른 식물에 10배 정도 많이 배출하는데 피톤치드는 피를 맑게하고 노폐물배출, 항생, 혈압강하 이뇨작용 등에 효과가 있고 공기 중 잡균을 죽이는 역할도 한다.','30분','출발지점과 중간 지점에 설치되어 있음','출발지점이 남원관광단지로 화장실들이 잘 갖춰져 있음','출발지점이 남원관광단지로 매점이 여러 곳 운영하고 있음','전북 남원시 어현동',35.401345,127.3931862);
