INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000624','태화강 100리길','02코스','망성교~범서 욱곡마을입구~사연마을~사연교~대방골~사연댐~한실마을~대곡마을~반구대암각화입구~천전리각석~대곡댐박물관','울산 울주군','보통','15~20Km미만','15','사일마을 뒷산 오솔길을 따라 걸으면서 사연댐의 전경을 한 눈에 볼 수 있고, 세계적인 문화유산인 반구대암각화, 천전리각석과 고려 말 유학자 포은 정몽주를 모신 반고서원, 대곡댐과 대곡박물관을 볼 수 있는 코스이다.','5시간30분','사전준비 필요','대곡댐박물관','없음','울산 울주군 언양읍 대곡리 산 130',35.5982816,129.1984956);
