INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000626','태화강 100리길','04코스','유촌마을(두서농공단지앞)~미호 하동마을~중동마을~상동마을~복안저수지~내와마을 삼거리~백운산 탑골샘','울산 울주군','보통','10~15Km미만','11','산길,계곡옆 오솔길, 농로, 마을길 등을 만날 수 있으며, 농업용수로 활용되는 복안저수지의 풍광은 압권이다.(단, 복안저수지에 차량출입은 불가함) 복안저수지에서 계곡을 따라 걷는 길과 백운산 입구에서 탑골샘으로 이어지는 산길은 태화강100리길의 백미이다.','3시간 30분','별도로 준비하셔야 함','','','울산 울주군 언양읍 대곡리 산 130',35.5982816,129.1984956);
