INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000628','중랑천 제방 산책로','01코스','군자교~장평교(1.4km)~장안교(1km)~배봉산 연육교(1km)','서울 동대문구','매우쉬움','1~5Km미만','3.8','동대문구 장안동과 휘경동 일대 중랑천 제방을 따라 조성된 산책로로 수령이 오래된 앙벚나무와 느티나무가 심어져 한여름철에도 시원한 그늘을 제공하고 있으며, 바닥은 산책과 조깅 등 이용시 무릎에 부담이 가지 않도록 탄성소재로 포장되어 있으며, 산책로 곳곳에 휴게쉼터와 전망데크, 운동기구 등이 설치되어 있어 산책로를 찾는 시민들에게 운동과 휴식, 여가활동 공간으로 사랑받고 있다.','1시간','없음(식수 준비요망)','장한평역, 제방 공중화장실(일정 거리마다 있음)','장한평역내 편의점','서울 중랑구 면목동',37.5750052,127.0774172);
--행 629
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000629','산수유 꽃시목지 걷는길','01코스','화전3리 마을입구(79국지도)에서 소하천 하류방향으로 신리마을까지 임.','경북 의성군','매우쉬움','1Km미만','0.6','30~300년생 산수유나무 3만여 그루가 군락을 이루고 있으며, 이른봄에는 노란꽃물결과 늦은 가을에는 빨간 열매로 마을경관이 아름답다. 경북 제일의 의성 사곡면 산수유 마을은 매년 3월이면 산수유의 만개가 절정을 이뤄 장관을 이룬다. 그야말로 꽃으로 치장한 마을이 만들어진다. 이 일대는 산과 논두렁, 도랑둑을 짙은 노란 물감으로 채색해 놓은 듯 산수유꽃의 행렬이 10리 넘게 이어진다. 골골에 골고루 흩어진 산수유의 노량 융단 물결은 특히 화전2리가 유명하다.','15분','시점에 있음','없음','없음','경북 의성군 사곡면 화전리 1337-1',36.2908941,128.7707656);
--행 630
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000630','산수유꽃피는 마을 생태탐방로','01코스','화전3리 마을입구(79국지도)에서 화전2리 마을 입구까지 임.','경북 의성군','매우쉬움','1~5Km미만','1.7','"살기좋은마을"로 선정된 산수유꽃피는 마을 사곡면 화전리 일대는 길이 8km, 넓이 6ha에 걸쳐 조선시대부터 자생한 15~300년 된 30,000그루의 산수유나무가 살아있는 화석처럼 군락을 이루고 있음, 이른 봄 산수유꽃을 보러오는 탐방객 및 가을 산수유열매를 보러 탐방객들이 많이 모이는 곳으로 산수유 군락지를 따라 자연 친환경적인 생태탐방로를 조성하여 생태관찰, 사진촬영, 산책로 등 탐방객들의 편의를 제공하고 있음, 매년 3월말에서 4월초 산수유꽃축제를 개최하여 다양한 볼거리 및 관광객들이 참여할 수 있는 프로그램을 운영하고 있음. 탐방로 시작지점은 사곡면 화전3리(하전.전풍) 마을에서 시작하여 화전2리 (숲실)마을까지 가는 길이며 탐방로는 시작점으로 부터 1.6km까지 조성되었으나, 그 후 계속 마을안까지 걸어서 탐방할 수 있음.','40분','없음','시점과 종점에 있음','없음','경북 의성군 사곡면 화전리 972-2',36.2796047,128.7756876);
--행 631
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000631','백제가요 정읍사 오솔길','03코스 정읍천변 자전거길','월영마을(문화광장) ~ 정읍천변 ~ 정읍고등학교 ~ 정읍사공원','전북 정읍시','보통','5~10Km미만','6.2','백제가요 정읍사 오솔길은 1300여 년 전 행상나간 남편을 기다리다가 망부석이 된 백제가요 정읍사 여인을 주 테마로 스토리와 천년사랑 프로그램을 가미하였다. 걷는 길은 완만하고 접근성이 매우 뛰어난 원점 회귀형 오솔길로 소나무 숲과 시누대 군락지, 내장호 수변 경환, 정읍천변 자전거 길로 이어지는 17.1km로 21세기 웰빙 명품 길이다. 그 중 3코스는 동진강의 발원지이자 생태의 젖줄인 정읍천변을 자전거를 이용하여 Tour biking을 즐기면서 생태하천 체험과 오솔길 탐방객이 출발점으로 돌아갈 수 있도록 원점 회귀형으로 조성하였다. 걷기뿐만 아니라 부담 없이 자전거를 타고 즐길 수 있는 코스로 자전거 타기 좋은 코스이다. 자전거 도로는 깔끔하게 정비되어 있고 주변경치도 좋아서 날씨가 따뜻한 날에 자전거를 대여해 내장산에서 불어오는 바람을 맞으며 달리기 좋다.','30분','문화광장 편의점에서 구입, 또는 사전준비','문화광장, 정읍시립박물관, 정읍사공원','문화광장 편의점','전북 정읍시 송산동',35.5270424,126.8767704);
