INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000632','무주 예향천리마실길','01코스 금강변','도소마을→(1.7km)대문바위→(0.9km)부남면소재지→(2.2km)벼룻길→(1.5km)각시바위→(3.3km)상굴암마을→(1.7km)굴암삼거리→(1.5km)잠두마을→(2.6km)요대마을→(2.1km)남대천→(2.5km)서면마을','전북 무주군','보통','15~20Km미만','19','금강 상류지역으로 마실길 곳곳에 볼거리와 전설이 깃든 관광자원들이 많이 있으며 옥녀봉 산줄기와 금강이 만나는 곳인 대문바위를 시작으로 굴암마을 대뜰에 물을 대기 위해 일제 강점기에 놓았던 농수로가 지금은 자연스럽게 사람들의 발길을 열어주는 소통의 길인 벼룻길이 되었는데 주민들은 이 길을 보뚝길이라고도 부름. 또한, 옛날 천상에서 내려온 선녀가 목욕을 하고 올라가다가 천의를 잃어버리고 오르지 못하자 인간세계에 남아 결혼하고 아들 셋을 낳았으나 후에 천의를 입고 올라가다 벼락을 맞고 떨어져 바위가 되었다는 각시바위도 있음. 걷는동안 눈을 즐겁게 시원하게 해주는 래프팅을 즐기는 사람들을 만날 수 있으며 더구나 반딧불이고장으로서 낮에는 아름다운 산과 들, 맑고 깨끗한 강물을 벗삼고, 밤에는 반딧불 불빛으로 신비탐사의 감동을 느낄 수 있는 즐거운 길이다.','6시간','별도의 식수보급시설이 없어 사전에 구입하거나 준비','부남면주민자치센터, 밤소체육공원, 굴암체육시설, 서면농특산물판매장','출발점 부남면소재지, 중간점 굴암리상점 외에는 없음','전북 무주군 부남면 굴암리',35.9969883,127.5975227);
