INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000663','진천 초롱길','01코스','초롱길 1구간 (농다리~하늘다리~농다리)','충북 진천군','쉬움','1~5Km미만','3.2','초롱길은 진천군이 지역의 대표적인 명소인 농다리와 초평호의 관광자원을 연계하여 조성한 수변탐방로와 트레킹 코스를 말하며 천년의 신비 농다리를 건너면 왼쪽으로는 1.7km의 농암정으로 이어지는 트레킹 코스가, 직진방향의 언덕길 너머에서는 탁 트인 초평호의 전망이 펼쳐진 수변탐방로가 시작된다. 수변탐방로는 초평호 주변 1km에 걸쳐 친환경 나무데크길로 조성돼있다. 경사가 거의없는 평지형태로 최근 조성된 둘레길 중에는 가장 편한 둘레길로 남녀노소 누구나 편하게 즐길 수 있다. 초평호를 가로질러 청소년수련원과 연결된 93m길이의 구름다리인 "하늘다리"가 놓여있으며 하늘다리 건너편 청소년수련원쪽에도 벤치와 전망대가 마련돼 초롱길 반대편에서의 풍광을 여유롭게 즐길 수 있다.','1시간','농다리~성황당 사이 식수대 있음','농다리전시관, 농다리입구, 성황당, 하늘다리','하늘다리 건너','충북 진천군 문백면 구곡리 129-3',36.828037,127.497749);
