INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000667','소백산 자락길','05코스 황금구만량길','기촌리 ~ (2.5km)매남치 ~ (2.6km)대대리(대곡초) ~ (1.6km)구만동 ~ (5.3km)보발분교 ~ (3km)보발재','충북 단양군','어려움','15~20Km미만','15.8','소백산자락길 05코스 황금 구만량길. 5자락은 구만동의 황금설화를 간직한 오감만족 여행길이다. 전국적으로 유명한 농촌체험마을, 한드미마을을 돌아오는 황금구만량길은 구만돌이 황금설화가 남아있는 길로써 소백산의 당당한 위엄을 보며 걸을 수 있는 옛길이다. 이 마을에서는 연중 다양한 산촌문화체험과 생태체험을 할 수 있으며, 동굴을 통해 소백산을 가로질러 순흥장을 봤다는 지금으로서는 믿기 힘든 애기도 들을 수 있다.','5시간','없음','대대리(마을입구)','대대리','충북 단양군 가곡면 보발리',37.0289931,128.4480478);
