INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000668','운문신화랑에코트레일','운문녹색길, 운문블루웨이, 운문숲길','방지초문명분교 - (0.79km)탐방안내센터 - (1.05km)솔바람길 이정표 - (1.41km)운문사주차장 - (2.07km)사리암주차장 - (1.80km)삼거리초소','경북 청도군','보통','1~5Km미만','3.3','청도 운문신화랑에코트레일은 기존의 운문사로 오르는 길을 정비한 길로서 남녀노소 걷기에 부담이 없는 길이다. 길은 크게 운문녹색길, 운문블루웨이, 운문숲길로 나누어져 있으며 운문산, 가지산, 상운산을 끼고 어우러져있다. 이중 운문녹색길은 신원리 방지초교문명분교에서 시작하여 사리암주자장을 거쳐 블루웨이와 이어지는 삼거리초소까지 이어지는 구간이다. 길도 쉽고 정비가 잘되어 있으며 운문사와 운문산, 운문천을 구경하여 걸을 수 있는 코스이다.','2시간','식수 보급처가 없으니 매점에서 구입하거나 사전준비','운문사 버스정류장, 운문사 주차장, 사리암 주차장','운문사 매표소 앞 매점, 사리암 주차장 매점','경상북도 청도군 운문면 신원리 1228-2',35.685304,128.9612171);
