INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000669','낙동강 예던길','낙동강 예던길','청량산 입구~관창1교(1.7km)','경북 봉화군','매우쉬움','1~5Km미만','1.7','강원도 태백에서 발원한 낙동강이 봉화군 명호면에 이르러 청량산을 끼고 돌며 수려한 경관을 만들어 놓았다. 낙동강변길은 이 뛰어난 경관을 감상하면서 남녀노소 누구나 쉽게 걸을 수 있는 길이다.','30분','식수보급처가 없으니 청량산도립공원 시설지구 슈퍼에서 구입하거나 사전준비','청량산안내소(간이화장실), 청산농원','출발점 인근 청량산도립공원 시설지구 슈퍼, 도착점 인근인 당산휴게소','경북 봉화군 명호면 북곡리 산 81',36.7957488,128.892235);
