INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000671','소백산 자락길','07코스 십승지 의풍옛길','영춘면사무소 ~ (5.5km)동대리 ~ (3.3km)의풍옛길 입구 ~ (4.5km)배틀재 ~ (4.9km)의풍리','충북 단양군','어려움','15~20Km미만','18.2','소백산자락길 07코스 십승지의풍옛길. 7자락은 남한강가에 깎아지른 석벽이 병풍처럼 늘어서 있는 장관을 구경할 수 있는 길이며, 정감록 십승지의 하나로 소개된 색다른 산골마을이다. 고려 때부터 소금을 운반하던 염로였고, 의풍리에는 정감록 십승지를 찾아온 사람들이 많이 살고 있다. 배틀재를 넘으면 조선후기 방랑시인 김삿갓을 만날 수 있다.','4시간 30분','없음','동대리','영춘면 소재지','충북 단양군 가곡면 보발리',37.0289931,128.4480478);
--행 672
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000672','대청호오백리길','07구간 부소담악길','대전시 동구 내탑동 꽃봉 갈림길 → 꽃봉 → 문화 류씨 묘→ 수생식물 학습원 → 방아실 회타운→ 방화정 → 문화 류씨 재실(마을회관) → 대정 삼거리 → 거먹골 → 항골 삼거리 → 공곡재 → 이평리 버스 종점(자연식당) → 절개지 → 보현사 → 서낭재가든 → 부소담악(부소무니) → 병풍바위 → 충북 옥천군 군북면 추소리 절골(느티나무, 돌탑)','충북 옥천군','어려움','10~15Km미만','14','와정 삼거리에서 출발하여 약 20분 가볍게 산행하면 작은 묘소와 소나무 한 그루가 있는 꽃봉 갈림길지점까지는 6구간과 같은 구간이다. 소나무 한 그루를 중심으로 왼쪽은 6구간인 주촌동으로 가는 길이고 오른쪽으로는 7구간 방향인 꽃봉으로 이어지는 산길이다. 우리는 우측으로 발길을 돌려 대정리 산성(이름 없는 산성이지만 마을 이름을 따 대정리 산성이라 명함)을 지나 꽃봉으로 올라간다. 꽃봉에서 내리막 산길을 걸어 문화 류씨 묘소까지 와서 오른쪽 수생식물 학습원과 방아실 방향으로 걸음을 옮긴다. 방아실에서 옥천 방향으로 거먹골과 항곡리를 지나 이평 2리를 향해 걸어간다. 이평리의 구불구불 공곡재를 넘어 고리산 농장식당에 도착해 잠시 목을 축이고 쉬었다 보현사와 서낭당가든 앞을 지나 아름다운 경치가 일품인 부소담에 올라 대청호를 감상한 뒤 되돌아 나와 추소리까지가 7구간의 끝이 된다.','6시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
--행 673
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000673','대청호오백리길','07-01구간 환산(고리산)길','공곡재 → 공곡재길 → 때림바위골 → 환산550봉 → 굴다리 → 이백삼거리 → 군북면사무소','충북 옥천군','보통','5~10Km미만','7','충북 옥천군 군북면 항곡리, 추소리, 증약리, 환평리에 걸쳐있는 산이다. 고도는 579m 이다. 환산은 예전에 봉수대가 있어서 조선시대 초기때부터 문헌에 기록된 산이다. ''세종실록지리지'' 에 "봉화가 2곳이니 월이산은 이산현 종쪽에 있고, 남쪽으로 영동 박달산에, 북쪽으로 본군 환산에 응한다. 환산은 군의 북족에 있다. 서쪽으로 회덕 계족산에 응한다." 라는 기록이 있다. ''신증동국여지승람''을 비롯하여 ''여지도서'', ''대동여지도'', ''1872년지방지도'' 등에도 환산 또는 환산봉수가 표시되어 있다.','약 3시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
--행 674
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000674','대청호오백리길','08구간 선비길','충북 옥천군 군북면 추소리 절골(느티나무) → 추소리 → 환평리 → 옥천식품의약품안정청 → 황골 갈림길 → 이지당 → 서화천 → 하늘농원 → 습지공원(공사중) → 양촌 → 보골(보오리) → 옥천폐기물처리장 → 이평마을 → 석결마을 → 옥천군 군북면 석호리 돌거리고개','충북 옥천군','어려움','10~15Km미만','13','서낭당 부소담악을 출발하여 추소리 성황당 고개에서 아기자기 마을길이 예쁜 환평리로 걸어 들어간다. 환평리 마노레스토랑 앞 삼거리에서 들판길로 접어들어 옥천약용식물재배시험장 앞을 가로질러 들길을 걸어 이지당을 향하여 걸어간다. 서낭당에서 약 5Km정도 걸어 도착한 이지당의 아름다운 건축물을 감상한 뒤 건물 앞에 유유히 흐르는 세화천 다리를 건너 지오리 마을을 통과하는 뒷길을 걸어 옥천폐기물처리장 앞을 지나 이평마을로 들어선다. 구불구불 이평마을을 걸어 내려와 석호리(석결)마을로 들어서면 대청호 풍광이 시원하게 펼쳐진다. 석결마을을 빠져나와 석호리와 진걸로 갈라지는 돌거리 삼거리에서 8구간을 마친다.','6시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
--행 675
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000675','왕피천 생태탐방로','02코스','굴구지 마을 ~ (4.6km)회귀 지점(거북바위 조망대) ~ (5.2km)굴구지 마을','경북 울진군','어려움','5~10Km미만','9.8','생태는 고정되어 있지 않고, 새처럼 움직이며 나뭇잎처럼 변화한다. 생태탐방은 이 움직임과 변화 속에 담긴 순환과 조화의 원리를 이해하고 자연자원이나 역사와 문화자원을 여러 지역에 걸쳐 이동하면서 체험하는 것을 말한다. 왕피천 생태탐방로는 강을 따라 걷다보면 산을 타고 오르기도 하고,어느새 바위들 사이로 길을 만들다가 이내 다시 발목까지 차오르는 왕피천의 생태탐방로는 다양한 생태자원과 자연 그대로의 모습을 즐길 수 있는 트레킹 명소이다. 2코스는 용소를 비롯해, 학소대, 거북바위 등 왕피천 협곡의 모습을 한 폭의 동양화로 펼쳐놓은 듯한 경관을 감상하기 위해서는 굴구지 마을을 거점으로 접근해야한다. ‘굴구지’는 굴같이 생긴 아홉구비를 넘는다는 뜻을 가진 구산 3리 마을의 고유 이름이다.','6시간','식수보급처가 없으니 매점에서 구입하거나 사전준비','굴구지 및 상천마을에 간이화장실','없음','경북 울진군 금강송면 왕피리 산 1',36.9070295,129.3173511);
--행 676
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000676','대청호오백리길','09구간 지용향수길','충복 옥천군 군북면 석호리 진걸 선착장 → 청풍정 → 돌거리 고개 → 국원 삼거리 → 신촌 식당가 → 37번 국도 교각 다리→ 임도 → 며느리재 → 326봉 → 마성산 → 섯바탱이길 → 310봉 → 교동저수지 옆 능선 → 육영수 생가 → 옥천향교 → 정지용 생가 → 옥천 교동리 구읍내','충북 옥천군','어려움','1Km미만','15','9구간의 시작 지점은 옥천군 군북면 석호리(돌거리고개) 삼거리에서 시작해 국원리 삼거리에서 37번 국도 건너편 큰엄마네 민박 뒷길 성왕로를 걸어 채석장을 지나 며느리재를 타고 409m의 해돋이 명소로 유명한 마성산을 오른다. 간혹 가파른 산길도 있지만 마성산 고갯길 삼거리에서 약 50분정도 산행하여 내려오면 옥천 교동리 육영수 생가 뒷담으로 내려오게 된다. 담을 돌아 정문으로 들어가 육여사의 생가를 돌아보고 향수길을 따라 약 200m 걸어가면 조선 중기 생원과 진사들이 모여 학문을 강론하던 옥주 사마소를 둘러본다. 사마소를 나와 우측으로 다시 걸음을 옮기면 우리나라 근대 시인의 대명사인 정지용 생가 나온다. 생가 바로 옆에 깔끔이 지어진 문학관에 들어가 정 시인의 삶과 시를 감상하고 생가를 들러보면 기억에 더 오래 남을 것이다. 이 구간은 산행과 근대 역사를 돌아보며 공부할 수 있는 코스가 함께 있어 아주 즐겁고 유익한 길이다.','5시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
--행 677
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000677','대청호오백리길','11구간 말티고개길','충북 옥천군 동이면 석탄리 고인돌공원 → 안터마을 (3.3km) → 지양리 갈림길 → 가리내농원 갈림길 → 피실 갈림길 → 탑산 갈림길 → 지양리 갈림길 → 옥천군 동이면 청마리마을(7.7km)','충북 옥천군','보통','10~15Km미만','11','옥천 안터마을(석탄리)에서 시작되는 11구간은 벽화가 예쁘게 그려진 안터마을 골목 안으로 걸어 들어가 반디불이 체험길인 동이청마임도로 걸어간다. 마치 원시림 속에 들어 온 것 같은 숲을 걸으며 온 몸을 정화시키며 걷는 기분과 어머니의 품속 같이 포근하고 편안해 지는 것을 금방 느낄 수가 있다. 임도를 따라 계속 걸어가면 피실 삼거리(생명강전원마을)에 이르게 된다. 그곳에서 우측 탑산(탑신리)방향으로 발길을 옮겨 걸어 올라간다. 주변은 원시림이지만 잘 닦여진 임도를 걷기에 그리 어렵고 힘들지가 않다. 탑산 정상 고개를 조금 내려오면 삼거리로 갈라지게 되는데 좌측의 청마리마을, 지금은 폐교가 된 청마초교(아자학교)까지 걸어간다.','3시간','식수보급처가 없으니 구입하거나 사전준비','옥천군 동이면 청마리 마을','안터마을에 1곳(안터슈퍼)','대전 대덕구 삼정동',36.4480599,127.4657027);
