INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000678','강동사랑길','06코스','당사항~용바위(0.6km)~까치골(1.1km)~당사항','울산 북구','보통','1~5Km미만','2.5','급하지 않은 경사와 당사항이 내려다 보이는 조망이 아름다운 길이다. 복잡한 마음의 짐을 던져두고 용바위와 넘섬의 옛 이야기를 생각하며 휘적 휘적 여유롭게 걸어가보자. 까치골 대밭에 사그락거리는 바람소리와 한적한 마을길의 적막함도 사색에는 좋은 약이 된다','1시간','','추억의 학교, 당사자연산직판장','사랑길 슈퍼','울산 북구 정자동',35.6151717,129.4438118);
