INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000688','대청호오백리길','11-1구간 지양 느티나무길','충북 옥천군 동이면 석탄리 안터마을 삼거리 → 지양리 → 말티','충북 옥천군','쉬움','5~10Km미만','7','옥천 석탄리 안터마을 삼거리에서 지장리와 양이동리 마을이 합쳐져 지양리라 불리는 마을로 들어선다. 탑산 아래 아늑하게 자리 잡은 자그마한 마을에 찬 봄바람에도 훈훈한 온기가 스민다. 마을 고갯마루의 오래된 느티나무 두 그루가 지금은 오지마을이 되었지만 그 옛날의 영화를 말해주고 있는 듯하다. 아기자기하게 이어져 있는 구불구불 돌담길을 걸어 마을을 돌아본 뒤 말티고개로 향한다. 분홍 복숭아꽃이 곱게 핀 과수원을 지나 올라설수록 시야가 넓게 펼쳐져 아름다운 초록 풍광인 말티고개를 넘어서 청마리로 가는 삼거리에서 마친다.','약 1시간 30분','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
