INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000691','대청호오백리길','14-2구간 용호수길','충북 옥천군 안내면 장고개 삼거리 → 군북면 막지리','충북 옥천군','어려움','10~15Km미만','(왕복)12','장고개에서 오른쪽으로 발길을 돌려 용호리마을로 향한다. 약 4km를 걸으면 용이 살았다는 호수라는 마을인 용호리마을을 오른쪽에 끼고 계속 앞으로 걸어 나간다. 마을에서 약 2km정도 풀과 흙길이 어우러진 카펫 같은 길을 걸어가면 진걸반도가 보이는 끝지점으로 갈 수 있다. 뺑둘러 있는 대청호와 산세를 바람과 함께 감상하고 다시 되돌아 임도 삼거리인 화골 삼거리에서 마친다.','약 6시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
