INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000692','대청호오백리길','15구간 구름고개길','충북 보은군 회남면 은운리 담양 1교 → 언목 마을 → 분저리 농촌체험 마을 → 충북 보은군 회남면 거교리','충북 옥천군','보통','10~15Km미만','14','옥천 안내면 답양리 양지골에서 가산천을 가르는 502번 도로의 왼쪽 방향으로 걸어 답양교와 구름이 은둔한다는 은운리마을을 지난다. 은운리부터는 비포장도로로 들어가지만 구불구불 이어진 길이라서 재미있게 분저리로 걸어갈 수 있다. 걷는 중간중간 왼쪽에는 대청호의 아름다운 풍경이 펼쳐져 걷는 이의 발걸음을 더욱 가볍게 해 준다. 약 6km의 산길을 내려오면 분저리에 도착한다. 마을 입구에는 폐교를 활용해 만든 ‘드림스쿨 예지원’과 정원과 각종 체육시설이 잘 갖춰진 분저리체험마을이 자리 잡고 있다. 마을 앞을 걸어 약 2.5km걸어가 판장대교를 건너 보은 방면인 조곡리 마을을 지나 거신교를 건너 회남면소재지 버스정류장에서 15구간을 마친다.','7시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
