INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000693','지리산둘레길','10코스(위태 ∼ 하동호)','위태(상촌) ? 지네재(1.9km) ? 오율마을(0.6km) ? 궁항마을(2.2km) ? 양이터재(2.2km) ? 나본마을(2.6km) ? 하동호(2km)','경남 하동군','어려움','10~15Km미만','11.5','지리산 둘레길 10코스(위태 ∼ 하동호). 숲속에서 깨우치다 ‘모든 것이 한가지였다. 생명’ 경상남도 하동군 옥종면 위태리에서 하동군 청암면에 위치한 하동호를 잇는 11.8km의 지리산둘레길. 이 구간은 낙동강수계권에서 식생이 다양한 섬진강수계권인 지리산 남쪽을 걷는 길이다. 지리산 영신봉에서 시작해 김해 분성산까지 이어지는 낙남정맥을 만나고, 상촌제, 궁항댐, 하동호의 시원한 풍경을 연출하는 저수지와 큰 댐을 볼 수 있다. 남명 조식선생과 지리산을 유람하는 선비들이 자주 찾았던 오대사터가 있던 백궁선원도 지난다. 아직도 손모를 하는 마을길을 걸으면서 산골마을의 고단한 삶에 고마움을 느낄 수 있다.','5시간','위태마을, 하동호','위태마을, 양이터재, 하동호','하동호 간이매점','전북 남원시 인월면 인월리',35.4592144,127.6018698);
--행 694
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000694','지리산둘레길','08코스(운리 ~ 덕산)','운리마을 ? 백운계곡(5.6km) ? 마근담입구(2.1km) ? 덕산(사리)(6.2km)','경남 산청군','어려움','10~15Km미만','13.9','작은 소리에 귀 기울이다 ‘지리산 나무야, 풀아, 돌아, 물아!’ 경상남도 산청군 단성면 운리에서 시천면 사리(덕산)를 잇는 13.1km의 지리산둘레길. 이 구간은 참나무가 우거진 숲길과 임도를 번갈아가며 걷는 길이다. 아기자기한 폭포와 소를 품고 있는 백운계곡을 가로질러 마근담과 운리로 이어지는 길은 청정한 숲의 기운을 담고 있다. 남명 조식 선생이 머루렀던 산천재가 있는 사리마을에서 바라보는 덕천강과 천왕봉은 아름다움과 굳센 기상을 담고 있어서 아이들을 동반한 가족들이 의미 있게 걸을 수 있는 길이다.','5시간','단속사지, 남명 조식기념관','운리주차장, 남명 조식기념관','운리마을, 덕산시장','전북 남원시 인월면 인월리',35.4592144,127.6018698);
