INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000695','울산어울길','02코스','염포삼거리(울산 북구 염포동 산141-6번지)~염포 팔각정~동축사 갈림길~다우림 목장~마골산헬기장~공군8146부대~무룡 임도방면~무룡(정자)고개','울산 북구','보통','10~15Km미만','10','염포삼거리에서 출발하여 임도를 따라 효문동과 정자동을 잇는 정자고개까지 걷는 코스로 코스 중간 중간에 바다와 시가지를 조망할 수 있는 전망대와 운동시설이 잘 갖추어져 있다. 대부분 임도로 되어 있어 봄,가을에는 매우 좋으나 여름에 햇볕이 강하게 내리쬐고 겨울에는 찬바람이 강한 곳이 있다.','5시간','','무룡고개','','울산 북구 가대동',35.6093334,129.2993885);
