INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000697','울산어울길','03코스','무룡(정자)고개 (울산 북구 어물동 산200-4번지)~무룡산 정상~전망대~야외공연장~달령재~동대산정상~연지암 갈림길~기령재(마우나오션 리조트 입구)','울산 북구','어려움','10~15Km미만','14.5','무룡(정자)고개에서 시작하여 무룡산 정상, 달령재, 동대산, 호계재, 마동재, 신흥재를 거쳐 기령까지 걷는 코스로 많은 고개가 있다. 시점인 정자고개에서 무룡산 정상까지 이어지는 길은 30분 가량 소요되며, 가파른 산길이고, 종점 기령에는 시내버스가 없어 매곡까지 걸어서 나와야 한다.','6시간','별도 준비해야 함','무룡고개, 기령, 달령재','','울산 북구 가대동',35.6093334,129.2993885);
