INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000699','금오서원 녹색길','금오서원 녹색길','금오서원 녹색길 5.6Km','경북 구미시','보통','1~5Km미만','4','금오서원 녹색길은 지역 문화 유산인 야은 길재선생의 충절을 기리기 위하여 구미시 선산읍 원리에 세운 금오서원 주변에 위치하며 인근에는 구미보 및 지역문화자원인 독동의 반송, 낙산고분군, 봉화대 등이 있어 낙동강 및 구미보의 경치를 느낄 수 있는 산책로 이다. 또한 낙동강 제8경(두루미의 군무), 구미보 하류의 뛰어난 경관을 볼 수 있다','1시간 30분','구미보 관리소','구미보 관리소','구미보 관리소','경북 구미시 선산읍 독동리 1-39',36.2386333,128.3399418);
