INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000700','대청호오백리길','18-1구간 마동 창작마을길','문덕교 → 묘암 삼거리 → 묘암리 → 마동 2교→ 마동 창작마을 (마동 1, 2리 버스 종점) → 묘암리 → 묘암 삼거리 → 문덕교','충북 청주시 청원구','쉬움','5~10Km미만','9','509번 지방도로 문덕교를 건너기 전 삼거리에서 마동창작마을 푯말이 표시된 방향인 묘암리로 걸어간다. 묘암리 버스정류장에서 왼쪽 좁은 시골길로 약 4km 정도 걸어가면 폐교된 회교국민학교가 마동창작마을로 변신해 화가, 조각가 등 예술가 3분이 둥지를 틀고 창작활동을 하시는 마동창작마을에 도착한다. 이곳에서 교실을 리모델링해 멋지고 운치있게 만든 갤러리 찻집에서 차 한 잔 마시고 조각과 그림 등 작품을 감상할 수 있다. 마동창작마을이 마을 끝이기 때문에 다시 들어왔던 묘암 삼거리로 돌아나오면 18구간 지선이 끝난다.','약 3시간','식수보급처가 없으니 구입하거나 사전준비','','','대전 대덕구 삼정동',36.4480599,127.4657027);
