INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000701','풍기인삼 개삼터길','풍기인삼 개삼터길','금계1리버스정류장(출발) ~ 금계중학교 ~ 금계2리 ~ 금계호 ~ 실맥이골(금강사) ~ 전망대 ~ 진밭골 ~ 용천골 ~ 금계1리(도착)','경북 영주시','보통','5~10Km미만','7','풍기인삼 최초 재배지이며, 정감록의 제1승지인 풍기읍 금계리 일원에 조성된 풍기인삼 개삼터 길은 전체코스가 7km로 약 2시간 10분 정도 소요 되며, 금선계곡 주변에는 정자, 쉼터 등을 설치하였으며, 탐방로에 장생이 녹색농촌체험마을이 있어 농산물 수확 등 현장농촌체험 관광을 함께 할 수 있는 길입니다.','2시간10분','식수보급처가 없으니 사전준비','출발(금선정)','없음','경북 영주시 풍기읍 금계리 산 17',36.8979456,128.5228043);
