INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000710','신성계곡 녹색길','01코스','보현요양원~갯버들하천길~방호정~솔밭쉼터~헌실교','경북 청송군','보통','1~5Km미만','4.7','보현요양원을 지나면 아름다운 물길 사이 갯버들이 군락을 이루는 갯버들 하천길을 따라 걷다보면 어느새 방호 조준도(1576~1665)가 1619년에 건립한 정자인 방호정을 지나게 된다. 선비들에게 낙원과 같던 청송의 이름난 청송팔경 가운데서도 주왕산과 주산지를 제치고 제1경으로 뽑힌 신성계곡 초입 절벽 낭떠러지 위에 느티나무와 벗하면 우뚝 솟아있는 정자가 바로 방호정이다. 방호정을 지나 징검다리를 건너 계곡 옆 소나무 숲 사이 솔밭쉼터가 자리하고 있으며 과수원하천길을 지나 헌실교에 진입하게 된다.','1시간 20분','방호정 옆','신성계곡 녹색길 안내센터 옆, 방호정 옆','방호정 앞 신성슈퍼 등 슈퍼 2개소 있음','경북 청송군 안덕면 신성리 53-4',36.3216324,128.9888927);
--행 711
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000711','걷고싶은 누리길','걷고싶은 누리길','각화저수지 ~(2.6㎞)바탈봉 ~ (1.6㎞)노고지리산 ~(1.3㎞) 장등동 버스종점','광주 북구','어려움','5~10Km미만','6.6','2007년 건설교통부의 「살고싶은 도시만들기 시범사업」 공모시 전국 1위로 선정된 각화동 시화(詩畵)마을과 연계함으로써 도·농간의 문화와 정취를 느끼면서 편안하게 걸을 수 있는 누리길을 조성하여 도시민들의 여가 공간으로 활용 가능하며, 본 노선과 무등산 청풍쉼터 사이에는 6Km의 자전거도로가 개설되어 있으며, 최근 외지인들에게도 널리 알려져 이용객이 급증하고 있는 무등산 옛길과도 연계됨으로써 무등산에 분포되어 있는 옛문화와 정취를 보다 더 많이 느낄 수 있어 도시민들의 여가공간으로서 활용도가 높은 길이다.','3시간','약수터 및 식수보급처가 없으니 사전준비 필요','문화대교 아래','산행구간에는 배점없음. 각화초 및 각화중 주변의 슈퍼 이용 가능.','광주 북구 망월동',35.1915703,126.9434839);
--행 712
INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000712','신성계곡 녹색길','02코스','헌실교~갈대 봇도랑길 ~ 자암 ~ 지소리마을','경북 청송군','보통','1~5Km미만','3','근곡리 마을을 지나 헌실교를 건너면 다리를 지나자 마자 왼편에 작은 봇도랑이 나온다. 봇도랑이란 ''봇물을 대거나 물이 빠지도록 만들어 놓은 도랑''을 뜻하는 순우리말인데 이름도 참 예쁘다. 그 옆으로 작은 길이 있는데 이 길은 가을이면 물가에 갈대가 우거지고, 간간히 큰 나무가 그늘을 드리운다. 이 길을 따라 조금만 더 내려가면 갑자기 하늘에서 뚝 떨어트린 것과 같이 붉게 물든 바위가 가로막아 길손을 놀라게 한다. 신성계곡의 아름다운 절경을 대표한다 해도 허언이 아닌 이곳을 자암이라 부른다. 마을 사람들에 의하면 붉은 언덕이란 뜻의 ''붉은 덤'', 또 붉은 벽이라 하여 ''적벽'', 혹은 붉게 생긴 병풍이라 ''붉은 병풍바위''등 애칭이 여러개다. 이를 지나 지소리 마을까지 진입하게 된다.','1시간','지소교 다리 밑','자암주변','지소리 마을','경북 청송군 안덕면 신성리 53-4',36.3216324,128.9888927);
