INSERT INTO WALKING_TRAIL (ESNTL_ID, WLK_COURS_FLAG_NM, WLK_COURS_NM, COURS_DC, SIGNGU_NM, COURS_LEVEL_NM, COURS_LT_CN, COURS_DETAIL_LT_CN, ADIT_DC, COURS_TIME_CN, OPTN_DC, TOILET_DC, CVNTL_NM, LNM_ADDR, COURS_SPOT_LA, COURS_SPOT_LO) VALUES ('KCCWSPO20N000000713','황룡강 누리길','01코스 황룡강 바람길','송산유원지 주차장 ~ 송산유원지 ~ 황계4교 ~ 입석마을 ~ 임곡교~ 생테하천조성구간 ~ 용진교 (8.6km)','광주 광산구','쉬움','5~10Km미만','8.6','시원하게 펼쳐진 황룡강과 들판(망월평야)를 바라보며, 백로를 벗삼아 바람을 따라 가는길, 먼 옛날 이곳을 지켜온 선조들의 흔적 앞에서 내일의 꿈과 희망을 속삭이는 길이다. 걷는 길 중간마다 쉼터가 있어 쉬엄쉬엄 걷기 좋은 길이다.','2시간 30분','식수 보급처 없음','송산 유원지, 용진교','송산 유원지에 카페있음','광주 광산구 등임동',35.1942701,126.769501);
